import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';

@Injectable({providedIn: 'root'})

export class DatiServizioService {
private readonly apiKey: string = '18e7c3ec20bb062fef1f033f043741c5';

 constructor(public readonly http:HttpClient) { }

  getDati(): void {
      this.http.get('../../assets/draghi.json').subscribe(dato =>{
        console.log(dato);
      });
  }

  getd(file: File): Observable<string>{
    // this.http.post('https://api.imgbb.com/1/upload?key=f846d53f241f112c5374bc04314dac91', dati).subscribe(qqqqq =>{
        
    //     console.log(qqqqq);
    //   });
    const formData = new FormData();
    formData.append('image', file);

    return this.http
    .post('/upload', formData, { params: { key: this.apiKey } })
    .pipe(map((responses) => responses['data']['url']));
  }
}
