import { Component } from '@angular/core';
import { DatiServizioService } from '../cummon/dati-servizio.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {

  nome: string="";
  password: string="";
  testoBottone: string;

  constructor(public service:DatiServizioService){

    this.testoBottone = "Premi per validarti";
    this.service.setAbilita(false);
  }

  abilita()
  {
    if(this.nome == "" || this.password == "" ){
      this.testoBottone = "inserisci la password e/o il nome utente";
    }
    else{
      this.testoBottone = "SEI validato";
    }


        // abilitato l'utente alla visualizzazione
        this.service.setAbilita(true);

  }
}
